#!/bin/bash
if [ -n "$($SHELL -c 'echo $ZSH_VERSION')" ]; then dir="$(dirname "$0")"; zsh "$(readlink -e "$dir")/support/install.sh"; elif [ -n "$($SHELL -c 'echo $BASH_VERSION')" ]; then dir="$(dirname "$BASH_SOURCE")"; dir="$(readlink -e "$dir")"; bash "$dir/init.sh"; bash "$dir/support/install.sh"; else echo 'Unhandled shell detected! Only bash and zsh are supported at this time.'; echo 'Please run this script using zsh or bash, or refer to README for install instructions.'; exit 1; fi
